import {
    CLEAR_ERRORS,
    GET_ERRORS
} from "./types";

// this will return errors
export const returnErrors = (message, status, id=null) => {
    return {
        type: GET_ERRORS,
        payload: {message, status, id}
    }
}

export const clearErrors = () => {
    return {
        type: CLEAR_ERRORS
    }
}
